import React, { useState, useEffect } from 'react';
import './App.css';
import flashcardsContent from './flashcards.md'; // This requires special webpack loader configuration
import { flashcardsData } from './flashcardsData';

function App() {
  const [flashcards, setFlashcards] = useState([]);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [correctCards, setCorrectCards] = useState([]);
  const [incorrectCards, setIncorrectCards] = useState([]);
  const [remainingCards, setRemainingCards] = useState([]);
  const [activeSection, setActiveSection] = useState('main'); // 'main', 'correct', or 'incorrect'

  useEffect(() => {
    // Start with hardcoded cards
    const hardcodedCards = [
      {
        id: 0,
        question: "Q1: What is React?",
        answer: "A1: React is a JavaScript library for building user interfaces."
      },
      {
        id: 1,
        question: "Q2: What is JSX?",
        answer: "A2: JSX is a syntax extension for JavaScript that looks similar to HTML."
      }
    ];

    setFlashcards(hardcodedCards);
    setRemainingCards([...Array(hardcodedCards.length).keys()]);

    // Parse the imported content
    if (flashcardsContent) {
      const parsedCards = parseMarkdown(flashcardsContent);
      if (parsedCards.length > 0) {
        setFlashcards(parsedCards);
        setRemainingCards([...Array(parsedCards.length).keys()]);
      }
    }

    // Parse the imported content from flashcardsData
    const parsedDataCards = parseMarkdown(flashcardsData);
    setFlashcards(parsedDataCards);
    setRemainingCards([...Array(parsedDataCards.length).keys()]);
  }, []);

  const parseMarkdown = (markdown) => {
    const lines = markdown.split('\n');
    const cards = [];
    let currentQuestion = '';
    let currentAnswer = '';
    let inQuestion = false;
    let inAnswer = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      if (line.startsWith('**Q') && line.includes(':**')) {
        // Save the previous Q/A pair if exists
        if (currentQuestion && currentAnswer) {
          cards.push({
            id: cards.length,
            question: currentQuestion,
            answer: currentAnswer
          });
        }

        // Start new question
        currentQuestion = line.replace(/\*\*/g, '');
        currentAnswer = '';
        inQuestion = true;
        inAnswer = false;
      } 
      else if (line.startsWith('**A') && line.includes(':**')) {
        inQuestion = false;
        inAnswer = true;
        currentAnswer = line.replace(/\*\*/g, '');
      }
      else if (inAnswer && line) {
        currentAnswer += '\n' + line;
      }
      else if (inQuestion && line) {
        currentQuestion += '\n' + line;
      }
    }

    // Add the last card if exists
    if (currentQuestion && currentAnswer) {
      cards.push({
        id: cards.length,
        question: currentQuestion,
        answer: currentAnswer
      });
    }

    return cards;
  };

  const handleFlip = () => {
    setShowAnswer(!showAnswer);
  };

  const handleCorrect = () => {
    const currentId = remainingCards[currentCardIndex];
    setCorrectCards([...correctCards, currentId]);
    handleNext();
  };

  const handleIncorrect = () => {
    const currentId = remainingCards[currentCardIndex];
    setIncorrectCards([...incorrectCards, currentId]);
    handleNext();
  };

  const handleNext = () => {
    setShowAnswer(false);
    if (currentCardIndex < remainingCards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    } else {
      // All cards have been reviewed
      setCurrentCardIndex(0);
      setActiveSection('main');
      setRemainingCards([...Array(flashcards.length).keys()].filter(
        id => !correctCards.includes(id) && !incorrectCards.includes(id)
      ));
    }
  };

  const switchSection = (section) => {
    setActiveSection(section);
    setCurrentCardIndex(0);
    setShowAnswer(false);
  };

  const getCurrentCards = () => {
    switch (activeSection) {
      case 'correct':
        return correctCards.map(id => flashcards[id]);
      case 'incorrect':
        return incorrectCards.map(id => flashcards[id]);
      default:
        return remainingCards.map(id => flashcards[id]);
    }
  };

  const currentCards = getCurrentCards();
  const currentCard = currentCards[currentCardIndex];

  if (!currentCard) {
    return (
      <div className="flashcard-app">
        <h1>Flashcards</h1>
        <div className="section-buttons">
          <button onClick={() => switchSection('main')}>Main Deck ({remainingCards.length})</button>
          <button onClick={() => switchSection('correct')}>Correct ({correctCards.length})</button>
          <button onClick={() => switchSection('incorrect')}>Incorrect ({incorrectCards.length})</button>
        </div>
        <div className="empty-message">
          {activeSection === 'main' 
            ? "You've completed all flashcards! Review your correct or incorrect cards." 
            : `No cards in the ${activeSection} section yet.`}
        </div>
      </div>
    );
  }

  return (
    <div className="flashcard-app">
      <h1>Flashcards</h1>

      <div className="section-buttons">
        <button onClick={() => switchSection('main')}>Main Deck ({remainingCards.length})</button>
        <button onClick={() => switchSection('correct')}>Correct ({correctCards.length})</button>
        <button onClick={() => switchSection('incorrect')}>Incorrect ({incorrectCards.length})</button>
      </div>

      <div className="progress">
        Card {currentCardIndex + 1} of {currentCards.length}
      </div>

      <div className="flashcard" onClick={handleFlip}>
        <div className={`flashcard-inner ${showAnswer ? 'flipped' : ''}`}>
          <div className="flashcard-front">
            <p>{currentCard.question}</p>
          </div>
          <div className="flashcard-back">
            <p>{currentCard.answer}</p>
          </div>
        </div>
      </div>

      <div className="controls">
        {showAnswer && activeSection === 'main' && (
          <>
            <button className="correct-btn" onClick={handleCorrect}>Got it right</button>
            <button className="incorrect-btn" onClick={handleIncorrect}>Need more practice</button>
          </>
        )}
        {(showAnswer && activeSection !== 'main') && (
          <button onClick={handleNext}>Next</button>
        )}
        {!showAnswer && (
          <button onClick={handleFlip}>Show Answer</button>
        )}
      </div>
    </div>
  );
}

export default App;
