import React, { useState, useEffect } from 'react';
import './App.css';
// import flashcardsContent from './flashcards.md'; // This requires special webpack loader configuration
import { flashcardsData } from './flashcardsData';

function App() {
  const [flashcards, setFlashcards] = useState([]);
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showAnswer, setShowAnswer] = useState(false);
  const [correctCards, setCorrectCards] = useState([]);
  const [incorrectCards, setIncorrectCards] = useState([]);
  const [remainingCards, setRemainingCards] = useState([]);
  const [activeSection, setActiveSection] = useState('main'); // 'main', 'correct', or 'incorrect'

  useEffect(() => {
    // Load the flashcards.md content
    fetch('/flashcards.md')
      .then(response => response.text())
      .then(text => {
        console.log("Loaded flashcards.md, length:", text.length);
        const parsedCards = parseFlashcards(text);
        console.log(`Parsed ${parsedCards.length} flashcards`);

        if (parsedCards.length > 0) {
          setFlashcards(parsedCards);
          setRemainingCards([...Array(parsedCards.length).keys()]);
        } else {
          console.error("No flashcards were parsed!");
          // Fallback to a few hardcoded cards
          const hardcodedCards = [
            {
              id: 0,
              question: "Q1: Test question?",
              answer: "A1: Test answer."
            }
          ];
          setFlashcards(hardcodedCards);
          setRemainingCards([...Array(hardcodedCards.length).keys()]);
        }
      })
      .catch(error => {
        console.error("Error loading flashcards:", error);
        // Use the existing hardcoded cards as fallback
        const hardcodedCards = [
          {
            id: 0,
            question: "Q2: How is a project defined in the context of software project management?",
            answer: "A2: A project is defined as a set of interrelated tasks with a specific goal, having defined start and end dates, funding limits, and resource consumption, ultimately aiming to produce a unique product, service, or result."
          },
          {
            id: 1,
            question: "Q3: What are the five key management functions described in Lecture 1?",
            answer: "A3: Planning: Establishing goals, developing strategies, forecasting, budgeting, and documenting project plans.\n\nOrganizing: Grouping tasks, establishing structures, assigning roles and responsibilities, and creating an organizational hierarchy.\n\nStaffing: Recruiting, training, evaluating, compensating, and sometimes replacing personnel to ensure the right people are in place.\n\nDirecting: Leading and supervising team members, motivating personnel, delegating authority, coordinating activities, and resolving conflicts.\n\nControlling: Monitoring progress, measuring performance against the plan, initiating corrective actions, and ensuring the project stays on track."
          },
          {
            id: 2,
            question: "Q4: Describe the 'four P's' a project manager must focus on.",
            answer: "A4: The four P's are:\n\nPeople: The team members and stakeholders involved.\n\nProduct: The final deliverables or outcomes of the project.\n\nProcess: The methods, procedures, and tools used to execute the project.\n\nProject: The overall structure, scope, and objectives of the endeavour."
          },
          {
            id: 3,
            question: "Q5: Why is effective planning crucial for the success of a software project?",
            answer: "A5: Effective planning sets a clear direction for the project by establishing goals, defining the necessary actions, and preparing for potential obstacles. It helps ensure that resources are used efficiently, deadlines are met, and risks are managed, all of which are critical to delivering a project on time and within budget."
          },
          {
            id: 4,
            question: "Q6: What is the difference between directing and controlling in project management?",
            answer: "A6: Directing involves leading and motivating the team, making decisions, delegating tasks, and ensuring smooth day-to-day operations.\n\nControlling is about monitoring progress, measuring performance against the established plan, and taking corrective actions to address deviations."
          }
        ];
        setFlashcards(hardcodedCards);
        setRemainingCards([...Array(hardcodedCards.length).keys()]);
      });
  }, []);

  // Function to parse all flashcards from the markdown content
  const parseFlashcards = (markdown) => {
    const cards = [];
    const lines = markdown.split('\n');

    let currentQuestion = '';
    let currentAnswer = '';
    let collectingAnswer = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      // Check for question pattern
      if (line.startsWith('**Q') && line.includes(':**')) {
        // If we were collecting an answer, save the previous Q&A pair
        if (currentQuestion && currentAnswer) {
          cards.push({
            id: cards.length,
            question: currentQuestion,
            answer: currentAnswer
          });
        }

        // Start new question
        currentQuestion = line.replace(/\*\*/g, '');
        currentAnswer = '';
        collectingAnswer = false;
      } 
      // Check for answer pattern
      else if (line.startsWith('**A') && line.includes(':**')) {
        collectingAnswer = true;
        currentAnswer = line.replace(/\*\*/g, '');
      }
      // If we're collecting an answer, add the line to the current answer
      else if (collectingAnswer && line) {
        currentAnswer += '\n' + line;
      }
    }

    // Add the last card if there is one
    if (currentQuestion && currentAnswer) {
      cards.push({
        id: cards.length,
        question: currentQuestion,
        answer: currentAnswer
      });
    }

    return cards;
  };

  const handleFlip = () => {
    setShowAnswer(!showAnswer);
  };

  const handleCorrect = () => {
    const currentId = remainingCards[currentCardIndex];
    setCorrectCards([...correctCards, currentId]);
    handleNext();
  };

  const handleIncorrect = () => {
    const currentId = remainingCards[currentCardIndex];
    setIncorrectCards([...incorrectCards, currentId]);
    handleNext();
  };

  const handleNext = () => {
    setShowAnswer(false);
    if (currentCardIndex < remainingCards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    } else {
      // All cards have been reviewed
      setCurrentCardIndex(0);
      setActiveSection('main');
      setRemainingCards([...Array(flashcards.length).keys()].filter(
        id => !correctCards.includes(id) && !incorrectCards.includes(id)
      ));
    }
  };

  const switchSection = (section) => {
    setActiveSection(section);
    setCurrentCardIndex(0);
    setShowAnswer(false);
  };

  const getCurrentCards = () => {
    switch (activeSection) {
      case 'correct':
        return correctCards.map(id => flashcards[id]);
      case 'incorrect':
        return incorrectCards.map(id => flashcards[id]);
      default:
        return remainingCards.map(id => flashcards[id]);
    }
  };

  const currentCards = getCurrentCards();
  const currentCard = currentCards[currentCardIndex];

  if (!currentCard) {
    return (
      <div className="flashcard-app">
        <h1>Flashcards</h1>
        <div className="section-buttons">
          <button onClick={() => switchSection('main')}>Main Deck ({remainingCards.length})</button>
          <button onClick={() => switchSection('correct')}>Correct ({correctCards.length})</button>
          <button onClick={() => switchSection('incorrect')}>Incorrect ({incorrectCards.length})</button>
        </div>
        <div className="empty-message">
          {activeSection === 'main' 
            ? "You've completed all flashcards! Review your correct or incorrect cards." 
            : `No cards in the ${activeSection} section yet.`}
        </div>
      </div>
    );
  }

  return (
    <div className="flashcard-app">
      <h1>Flashcards</h1>

      <div className="section-buttons">
        <button onClick={() => switchSection('main')}>Main Deck ({remainingCards.length})</button>
        <button onClick={() => switchSection('correct')}>Correct ({correctCards.length})</button>
        <button onClick={() => switchSection('incorrect')}>Incorrect ({incorrectCards.length})</button>
      </div>

      <div className="progress">
        Card {currentCardIndex + 1} of {currentCards.length}
      </div>

      <div className="flashcard" onClick={handleFlip}>
        <div className={`flashcard-inner ${showAnswer ? 'flipped' : ''}`}>
          <div className="flashcard-front">
            <p>{currentCard.question}</p>
          </div>
          <div className="flashcard-back">
            <p>{currentCard.answer}</p>
          </div>
        </div>
      </div>

      <div className="controls">
        {showAnswer && activeSection === 'main' && (
          <>
            <button className="correct-btn" onClick={handleCorrect}>Got it right</button>
            <button className="incorrect-btn" onClick={handleIncorrect}>Need more practice</button>
          </>
        )}
        {(showAnswer && activeSection !== 'main') && (
          <button onClick={handleNext}>Next</button>
        )}
        {!showAnswer && (
          <button onClick={handleFlip}>Show Answer</button>
        )}
      </div>
    </div>
  );
}

export default App;
